import React from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux';

const Alert = ({ alerts }) => 
alerts !==  null && 
alerts.length > 0 && 
alerts.map(alert => {
    <div key={alert.id} className={`alert alert-${alert.alertType}`}>
        {alert.msg}
    </div>
});

Alert.propTypes = {
   alerts : PropTypes.array.isRequired
};


//so we are going to fetch componets here to show to the user

const mapStateToProps = state => ({          //  maping state with props

    alerts: state.alert  // this is the  alert from the reducer

})

export default connect(mapStateToProps)(Alert);
