import React , {Fragment} from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux';
import  Moment  from 'react-moment';
import {Link} from 'react-router-dom';
import {addLike , removeLike , deletePost} from '../../actions/post'

const PostItem = ({deletePost,addLike ,removeLike, auth , post : {_id , text , name , avator , user , likes , comments , date}
})=>  (<div class="post bg-white p-1 my-1">
        <div>
          <Link to={`/profile/${user}`}>
            <img
              class="round-img"
              src={"//www.gravatar.com/avatar/096020b84d1fe75463eb00482350fb56?s=200&r=pg&d=mm"}
              alt=""
            />
            <h4>{name}</h4>
          </Link>
        </div>
        <div>
          <p class="my-1">{text} </p>
           <p class="post-date">
              Posted on <Moment format='YYYY/MM/DD'>{date}</Moment>
          </p>
          {showActions && <Fragment>
            <button onClick={e => addLike(_id)} type="button" class="btn btn-light">
            <i class="fas fa-thumbs-up"></i>{' '}
           <span>{likes.length > 0 && <span >{likes.length}</span>}</span> 
          </button>
          <button onClick={e => removeLike(_id)} type="button" class="btn btn-light">
            <i class="fas fa-thumbs-down"></i>
          </button>
          <Link to={`/post/${_id}`} class="btn btn-primary">
            Discussion{' '}{comments.length > 0 && <span class='comment-count'>{comments.length}</span>}
          </Link>
          {!auth.loading && user === auth.user._id && (
          <button  onClick={e => deletePost(_id)} type="button"
          class="btn btn-danger">
          <i class="fas fa-times"></i>
        </button>
          )}
          </Fragment>}

        </div>
      </div>
)

PostItem.defaultProps = {
  showActions : true
}

PostItem.propTypes = {

    post: PropTypes.object.isRequired,
    auth: PropTypes.object.isRequired,
    addLike: PropTypes.func.isRequired,
    removeLike: PropTypes.func.isRequired,
    deletePost:PropTypes.func.isRequired
}

const mapStateToProps = state => ({
    auth : state.auth
})
export default connect(mapStateToProps , {addLike , removeLike , deletePost}) (PostItem)
