import React , {Fragment} from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux';
import  Moment  from 'react-moment';
import {Link} from 'react-router-dom';

const PostItem = ({auth , post : {_id , text , name , avator , user , likes , comments , date}
})=>  (<div class="post bg-white p-1 my-1">
        <div>
          <Link to="/profile">
            <img
              class="round-img"
              src={"//www.gravatar.com/avatar/096020b84d1fe75463eb00482350fb56?s=200&r=pg&d=mm"}
              alt=""
            />
            <h4>{name}</h4>
          </Link>
        </div>
        <div>
          <p class="my-1">
            {text}
          </p>
           <p class="post-date">
              Posted on <Moment format='YYYY/MM/DD'>{date}</Moment>
          </p>
          <button type="button" class="btn btn-light">
            <i class="fas fa-thumbs-up"></i>
            {likes.length > 0 && <span class='comment-count'>{likes.length}</span>}
          </button>
          <button type="button" class="btn btn-light">
            <i class="fas fa-thumbs-down"></i>
          </button>
          <Link to={`/post/${_id}`} class="btn btn-primary">
            Discussion{' '}{comments.length > 0 && <span class='comment-count'>{comments.length}</span>}
          </Link>
          {!auth.loading && user === auth.user._id && (
          <button  type="button"
          class="btn btn-danger">
          <i class="fas fa-times"></i>
        </button>
          )}
        </div>
      </div>
)


PostItem.propTypes = {

    post: PropTypes.object.isRequired,
    auth: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    auth : state.auth
})
export default connect(mapStateToProps , {}) (PostItem)
