import {v4 as uuid} from 'uuid'; 

import {SET_ALERT , REMOVE_ALERT} from  './types';

export const setAlert = (msg , alertType) => dispatch => {
  const id = uuid();
  dispatch({
      type :  SET_ALERT,
      payload : { msg , alertType , id}
  });


setTimeout(() => dispatch({
  type:REMOVE_ALERT,
  payload: id
  }), 4000);
};

//use to help us export the alerts to alert.js present in reducers
